      this.handleSubmit = this.handleSubmit.bind(this);
    }

    taketoreport(event){
        window.location.href="/report";
    }

    handleChange(event) {
      this.setState({
          img: event.target.img,
          address:event.target.address,
          city:event.target.city,
          pin:event.target.pin,
