                    <label for="City" class="page3-2-2" >City</label>
                    <label for="State" class="page3-2-2-1">State</label>
                    <label for="Pin" class="page3-2-2-1">Pin</label><br/>
                    <input type="text" class="City page3-2-2-2" name="City" required value={this.state.city} onChange={this.handleChange}/>
                    <input type="text" class="State page3-2-2-2" name="State" required value={this.state.state} onChange={this.handleChange}/>
                    <input type="number" class="pin page3-2-2-2" name="Pin" required value={this.state.pin} onChange={this.handleChange}/><br/>

                    <label for="Cont" class="page3-2-2">Contact Number</label><br/>
                    <input type="number" class="add page3-2-2-2" name="Cont" required value={this.state.number} onChange={this.handleChange} /><br/>

                    <label for="Title" class="page3-2-2">Title</label><br/>


                    <label for="Details" class="page3-2-2">Details</label><br/>
                    <input type="text" class="Details page3-2-2-2" name="Details" required value={this.state.desc} onChange={this.handleChange}/><br/>

                    <input type="submit" class="submit" value="Submit" />
            </form> 


            </div>

        </div>
      );
    }
  }

export default Form;
